member.example:
[C#]
BarCodeBuilder b = new BarCodeBuilder();
b.SymbologyType = Symbology.AustraliaPost;
b.CustomerInformationInterpretingType = CustomerInformationInterpretingType.CTable;
b.CodeText = "5912345678ABCde";

using (BarCodeReader r = new BarCodeReader(b.BarCodeImage, DecodeType.AustraliaPost))
{
   r.CustomerInformationInterpretingType = CustomerInformationInterpretingType.CTable;
   while (r.Read())
   {
       Console.WriteLine("BarCode Type: " + r.GetCodeType());
       Console.WriteLine("BarCode CodeText: " + r.GetCodeText());
   }
}

[VB.NET]
Dim b As BarCodeBuilder
b = New BarCodeBuilder()
b.SymbologyType = Symbology.AustraliaPost
b.CustomerInformationInterpretingType = CustomerInformationInterpretingType.CTable
b.CodeText = "5912345678ABCde"

Dim r As BarCodeReader
r = New BarCodeReader(b.BarCodeImage, DecodeType.AustraliaPost)
r.CustomerInformationInterpretingType = CustomerInformationInterpretingType.CTable
While r.Read()
   Console.WriteLine("BarCode Type: " + r.GetReadType())
   Console.WriteLine("BarCode CodeText: " + r.GetCodeText())
End While
r.Close()

